// CoffeeCard.js
import React from "react";
import { useDispatch } from "react-redux";
import { addSale } from "../redux/cafeSlice"; // Import the addSale action

const CoffeeCard = ({ coffee }) => {
  const dispatch = useDispatch();
  
  const handleOrder = () => {
    // Add sale to the store
    const sale = {
      id: Date.now(), // Generate a unique ID
      coffeeType: coffee.name,
      quantity: 1, // Default quantity = 1
      price: coffee.price, // Adjust based on your API structure
      total: coffee.price, // Total price = price * quantity
    };
    dispatch(addSale(sale)); // Dispatch the sale
  };

  return (
    <div className="col-md-4" key={coffee.id}>
      <div className="card mb-4 shadow-sm">
        <img src={coffee.image} alt={coffee.name} className="card-img-top" />
        <div className="card-body">
          <h5 className="card-title">{coffee.name}</h5>
          <p className="card-text">{coffee.description}</p>
          <button className="btn btn-primary" onClick={handleOrder}>
            Commander
          </button>
        </div>
      </div>
    </div>
  );
};

export default CoffeeCard;
