//page pour l'affichage des details d'un equipement
import React from 'react';
import { useParams, useLocation } from 'react-router-dom';

function PageDetails({ equips }) {
  const { id } = useParams();
  const location = useLocation();
  const equip = equips.find(equip => equip.id === parseInt(id));

  if (!equip) {
    return <div>Équipement non trouvé</div>;
  }

  return (
    <div>
      <h2>Équipement: {equip.name}</h2>
      <p>Date d'achat: {equip.purchaseDate ? equip.purchaseDate : 'N/A'}</p>
    </div>
  );
}

export default PageDetails;
