import React, { useState } from 'react';

const FiltreVille = ({ clients }) => {
    const [ville, setVille] = useState('');
    const filteredClients = clients.filter(client => client.ville.toLowerCase() === ville.toLowerCase());

    return (
        <div>
            <h2>Filtrer par ville</h2>
            <input
                type="text"
                placeholder="Entrez la ville..."
                value={ville}
                onChange={(e) => setVille(e.target.value)}
            />
            <ul>
                {filteredClients.length > 0 ? (
                    filteredClients.map(client => (
                        <li key={client.id}>{client.nom} - {client.Tel}</li>
                    ))
                ) : (
                    <li>Aucun client trouv√© pour cette ville.</li>
                )}
            </ul>
        </div>
    );
};

export default FiltreVille;
