import React, { useState } from "react";
function MAJClient() {
  const [client, setClient] = useState({
    id: 1,
    nom: "SA SARL",
    Tel: "0604404040",
    ville: "Rabat",
    CA: 15000,
  });

  const handleChange = (e) => {
    setClient({ ...client, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2>Mettre a Jour Client</h2>
      <form>
        <label>
          Nom:{" "}
          <input
            type="text"
            name="nom"
            value={client.nom}
            onChange={handleChange}
          />
        </label>
        <label>
          Ville:{" "}
          <input
            type="text"
            name="ville"
            value={client.ville}
            onChange={handleChange}
          />
        </label>
        <label>
          Tel:{" "}
          <input
            type="text"
            name="Tel"
            value={client.Tel}
            onChange={handleChange}
          />
        </label>
        <label>
          Chiffre d'Affaires:
          <input
            type="number"
            name="CA"
            value={client.CA}
            onChange={handleChange}
          />
        </label>
      </form>
    </div>
  );
}

export default MAJClient;
