import React from "react";
import { useSelector } from "react-redux";

const Accueil = () => {
  // Accéder à l'état 'gestionArgent' du store Redux
  const gestionArgent = useSelector((state) => state.gestionArgent);

  // Vérification si l'état 'gestionArgent' existe avant d'y accéder
  if (!gestionArgent) {
    return <div>Chargement...</div>; // En attendant que l'état soit défini
  }

  // Accéder à 'compteur' dans l'état
  const compteur = gestionArgent.compteur;

  return (
    <div>
      <h1>Comptoir : {compteur}</h1>
    </div>
  );
};

export default Accueil;
