import React, { useState } from "react";


export default function AjouterVoiture({onAjout}) {
    const [nouvelleVoiture, setNouvelleVoiture] = useState({
        id: '',
        Marque: '',
        TypeCarburant: '',
        PrixLocation: '',
        image: '',
        discount: 0,
    });
    
    const [showDiscount , setShowDiscount] = useState(false);


    const handleChange = (e) => {
        setNouvelleVoiture({
            ...nouvelleVoiture,
            [e.target.name]: e.target.value
        });
    };

    const handleDiscountChange = (e) => {
        setNouvelleVoiture({
            ...nouvelleVoiture,
            discount: e.target.value
        });
    };

    const handleSubmit = (e) => {
        /*
            * âœ…EmpÃªche le rechargement de la page avec e.preventDefault()
        */
        e.preventDefault();
        
        if (nouvelleVoiture.id && nouvelleVoiture.Marque && nouvelleVoiture.PrixLocation) {
            onAjout(nouvelleVoiture); //* ðŸ”¥ Envoie Ã  ListeVoitures
            setNouvelleVoiture({ id: "", Marque: "", TypeCarburant: "", PrixLocation: "", image: "" });
        } else {
            alert("Veuillez remplir tous les champs obligatoires !");
        }
    };

    const discountedPrice = nouvelleVoiture.discount ? nouvelleVoiture.PrixLocation - (nouvelleVoiture.PrixLocation * (nouvelleVoiture.discount / 100)) : nouvelleVoiture.PrixLocation;

    return (
        <div>
            <h1>Gestion des Voitures de Locations</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="id" placeholder="ID" value={nouvelleVoiture.id} onChange={handleChange} />
                <input type="text" name="Marque" placeholder="Marque" value={nouvelleVoiture.Marque} onChange={handleChange} />
                <input type="text" name="TypeCarburant" placeholder="Type Carburant" value={nouvelleVoiture.TypeCarburant} onChange={handleChange} />
                <input type="number" name="PrixLocation" placeholder="Prix de Location" value={nouvelleVoiture.PrixLocation} onChange={handleChange} />
                <input type="text" name="image" placeholder="Image URL" value={nouvelleVoiture.image} onChange={handleChange} />

                {/* Checkbox pour afficher l'input discount */}
                <label>
                    Ajouter un discount
                </label>
                <input type="checkbox" onChange={() => setShowDiscount(!showDiscount)} />
                
                {showDiscount && (
                    <div>
                        <input type="number"
                            name="discount" placeholder="Discount en %" value={nouvelleVoiture.discount} onChange={handleDiscountChange} />
                    </div>
                )}

                <button type="submit" style={{ background: "blue", color: "white" }}>
                    Ajouter
                </button>
            </form>
        </div>
    )
};
