import React from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
// import { incrementer, decrementer, supprimer } form './store';

const ElemProduit = ({ produit }) => {
  const dispatch = useDispatch();

  return (
    <tr key={produit.id}>
      <td>
        <Link to={`/Detail/${produit.id}`}>{produit.id}</Link>
      </td>
      <td>{produit.nom}</td>
      <td>{produit.prix} DH</td>
      <td>
        <button onClick={() => dispatch(decrementer(produit.id))}>-</button>
        {produit.qte}
        <button onClick={() => dispatch(incrementer(produit.id))}>+</button>
      </td>
      <td>{produit.prix * produit.qte}</td>
      <td>
        <button onClick={() => dispatch(supprimer(produit.id))}>
          supprimer
        </button>
      </td>
    </tr>
  );
};

export default ElemProduit;
