import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Accueil from "./Accueil";
import Liste from "./Lister";
import "./App.css";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [solde, setSolde] = useState(0);

  const saveTransaction = (nom, montant, type) => {
    const montantFinal = type === "+" ? parseFloat(montant) : -parseFloat(montant);
    setSolde(solde + montantFinal);

    const newTransaction = { nom, montant: montantFinal, type };
    setTransactions([...transactions, newTransaction]);
  };

  return (
    <Router>
      <div className="App">
        <nav>
          <ul>
            <li><Link to="/">Accueil</Link></li>
            <li><Link to="/liste">Liste</Link></li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Accueil solde={solde} saveTransaction={saveTransaction} />} />
          <Route path="/liste" element={<Liste transactions={transactions} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
