import React, { useState } from 'react';
import FormEquip from '../composants/FormEquip';
import ListEquip from '../composants/ListEquip';

function PageAccueil() {
  const [equipements, setEquipements] = useState([]);

  const addEquipement = (equipement) => {
    setEquipements([...equipements, { ...equipement, id: Date.now() }]);
  };

  const deleteEquipement = (id) => {
    setEquipements(equipements.filter(equip => equip.id !== id));
  };

  const toggleReformer = (id) => {
    setEquipements(equipements.map(equip => 
      equip.id === id ? { ...equip, aReformer: !equip.aReformer } : equip
    ));
  };

  return (
    <div>
      <FormEquip onAdd={addEquipement} />
      <ListEquip 
        equipements={equipements} 
        deleteEquipement={deleteEquipement} 
        toggleReformer={toggleReformer} 
      />
    </div>
  );
}

export default PageAccueil;
