import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addItem } from '../slices/';

const ItemForm = () => {
  const dispatch = useDispatch();
  const [formData, setFormData] = useState({ id: '', name: '', price: '', quantity: 1 });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addItem({ ...formData, price: parseFloat(formData.price), quantity: parseInt(formData.quantity) }));
    setFormData({ id: '', name: '', price: '', quantity: 1 });
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4">
      <div className="form-group">
        <label>ID</label>
        <input type="text" name="id" value={formData.id} onChange={handleChange} className="form-control" required />
      </div>
      <div className="form-group">
        <label>Nom</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} className="form-control" required />
      </div>
      <div className="form-group">
        <label>Prix</label>
        <input type="number" name="price" value={formData.price} onChange={handleChange} className="form-control" required />
      </div>
      <div className="form-group">
        <label>Quantit√©</label>
        <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} className="form-control" required />
      </div>
      <button type="submit" className="btn btn-primary">Ajouter au panier</button>
    </form>
  );
};

export default ItemForm;
