// src/Maj.js

import React, { useState } from 'react';

const Maj = ({ clients, setDepenses }) => {
    const [formData, setFormData] = useState({ nom: '', Tel: '', ville: '', CA: 0 });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setDepenses([...clients, { id: clients.length + 1, ...formData }]);
        setFormData({ nom: '', Tel: '', ville: '', CA: 0 }); // Reset form
    };

    return (
        <div>
            <h2>Ajouter ou mettre à jour un client</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="nom"
                    placeholder="Nom"
                    value={formData.nom}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="Tel"
                    placeholder="Téléphone"
                    value={formData.Tel}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="ville"
                    placeholder="Ville"
                    value={formData.ville}
                    onChange={handleChange}
                    required
                />
                <input
                    type="number"
                    name="CA"
                    placeholder="Chiffre d'Affaires"
                    value={formData.CA}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Enregistrer</button>
            </form>
        </div>
    );
};

export default Maj;
