import { configureStore, createSlice } from '@reduxjs/toolkit';

/*
 ! --- Slice Redux pour le panier ---
 * Crée un slice pour gérer l'état du panier
 * Définit les actions pour ajouter, incrémenter, décrémenter et supprimer des produits
 * Exporte les actions et le reducer
 */


const panierSlice = createSlice({
    name: 'panier',
    initialState: [],
    reducers: {
        ajouter: (state, action) => {
            const { id, nom, prix, qte } = action.payload;
            const product = state.find((item) => item.id === id);
            if (!product) {
                state.push({ id, nom, prix, qte });
            }
        },
        incrementer: (state, action) => {
            const product = state.find((item) => item.id === action.payload);
            if (product) {
                product.qte += 1;
            }
        },
        decrementer: (state, action) => {
            const product = state.find((item) => item.id === action.payload);
            if (product && product.qte > 1) {
                product.qte -= 1;
            }
        },
        supprimer: (state, action) => {
            return state.filter((item) => item.id !== action.payload);
        }
    }
});

export const { ajouter, incrementer, decrementer, supprimer } = panierSlice.actions;
export const panierReducer = panierSlice.reducer;

export const store = configureStore({
    reducer: {
        panier: panierReducer
    }
});
