//page pour l'affichage des details d'un equipement
import React from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';

function PageDetails({ equipements }) {
  const { id } = useParams();
  const equipement = equipements.find(equipement => equipement.id === parseInt(id));

  if (!equipement) {
    return <div>Équipement non trouvé</div>;
  }

  return (
    <div>
      <h2>Équipement: {equipement.nom}</h2>
      <p>Date d'achat: {equipement.dateAchat ? equipement.dateAchat : 'N/A'}</p>
      <Link to="/" className="btn btn-primary">Retour à la page d'accueil</Link>
    </div>
  );
}

export default PageDetails;
