// src/FiltreCa.js

import React, { useState } from 'react';

const FiltreCa = ({ clients }) => {
    const [minCA, setMinCA] = useState(0); // État pour le montant minimum de CA
    const [filteredClients, setFilteredClients] = useState(clients); // État pour stocker les clients filtrés

    // Fonction pour filtrer les clients par CA
    const filterClientsByCA = () => {
        const result = clients.filter(client => client.CA >= minCA);
        setFilteredClients(result);
    };

    return (
        <div>
            <h2>Filtrer par Chiffre d'Affaires</h2>
            <input
                type="number"
                placeholder="Chiffre d'Affaires minimum"
                value={minCA}
                onChange={(e) => setMinCA(e.target.value)}
                onBlur={filterClientsByCA} // Appeler la fonction de filtrage quand le champ perd le focus
            />
            <button onClick={filterClientsByCA}>Filtrer</button>
            <ul>
                {filteredClients.length > 0 ? (
                    filteredClients.map(client => (
                        <li key={client.id}>{client.nom} - {client.Tel} - CA: {client.CA}</li>
                    ))
                ) : (
                    <li>Aucun client trouvé avec ce CA.</li>
                )}
            </ul>
        </div>
    );
};

export default FiltreCa;
