import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

function PageDetails({ equipements }) {
  const { id } = useParams(); // Récupérer l'ID depuis l'URL
  const equipement = equipements.find(
    (equipement) => equipement.id === parseInt(id)
  );

  if (!equipement) {
    return <div>Équipement non trouvé</div>; // Si l'équipement n'existe pas, afficher un message
  }

  return (
    <div>
      <h2>Équipement: {equipement.nom}</h2>
      <p>Date d'achat: {equipement.dateAchat ? equipement.dateAchat : "N/A"}</p>
      <p>Statut: {equipement.estReforme ? "À réformer" : "Actif"}</p>
      <Link to="/" className="btn btn-primary">
        Retour à la page d'accueil
      </Link>
    </div>
  );
}

export default PageDetails;
