import React from 'react';

//affichage d'equipement individuel

function EquipI({ equip, onReform, onDelete }) {
  return (
    <div style={{ backgroundColor: equip.isReformed ? 'lightcoral' : 'white' }}>
      <h3>{equip.name}</h3>
      <p>{equip.purchaseDate ? `Date d'achat: ${equip.purchaseDate}` : 'Date d\'achat: N/A'}</p>
      <button onClick={() => onReform(equip.id)}>A r√©former</button>
      <button onClick={() => onDelete(equip.id)}>Supprimer</button>
    </div>
  );
}

export default EquipI;
