// HotCafe.js et IceCafe.js (ajout du bouton "Commander")
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setHotCafe, setLoading, setError, clearLoading, addSale } from "../redux/cafeSlice"; // Importer addSale
import axios from "axios";

const CoffeeCard = ({ coffee }) => {
  const dispatch = useDispatch();
  
  const handleOrder = () => {
    // Ajouter la vente au store
    const sale = {
      id: Date.now(), // Génère un ID unique
      coffeeType: coffee.name,
      quantity: 1, // Par défaut, quantité = 1
      price: coffee.price, // Vous devrez peut-être ajuster la structure des données
      total: coffee.price, // Total initial = prix unitaire
    };
    dispatch(addSale(sale)); // Ajouter la vente
  };

  return (
    <div className="col-md-4" key={coffee.id}>
      <div className="card mb-4 shadow-sm">
        <img src={coffee.image} alt={coffee.name} className="card-img-top" />
        <div className="card-body">
          <h5 className="card-title">{coffee.name}</h5>
          <p className="card-text">{coffee.description}</p>
          <button className="btn btn-primary" onClick={handleOrder}>
            Commander
          </button>
        </div>
      </div>
    </div>
  );
};

const HotCafe = () => {
  const dispatch = useDispatch();
  const { iceCafe, loading, error } = useSelector((state) => state.cafe);

  useEffect(() => {
    const fetchIceCafe = async () => {
      dispatch(setLoading());
      try {
        const response = await axios.get("https://api.sampleapis.com/coffee/iced");
        dispatch(setIceCafe(response.data));
      } catch (err) {
        dispatch(setError(err.message));
      } finally {
        dispatch(clearLoading());
      }
    };
    fetchIceCafe();
  }, [dispatch]);

  return (
    <div>
      <h1 className="mb-4">Ice Cafe</h1>
      {loading ? (
        <p>Chargement...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div className="row">
          {iceCafe.map((item) => (
            <CoffeeCard key={item.id} coffee={item} />
          ))}
        </div>
      )}
    </div>
  );
};

export default IceCafe;
