// src/FiltreCa.js

import React, { useState } from "react";

const FiltreCa = ({ clients }) => {
  const [minCA, setMinCA] = useState(""); // État pour le montant minimum de CA
  const filteredClients = clients.filter(
    (client) => client.CA >= Number(minCA)
  ); // Filtrage des clients par CA

  return (
    <div>
      <h2>Filtrer par Chiffre d'Affaires</h2>
      <input
        type="number"
        placeholder="Chiffre d'Affaires minimum"
        value={minCA}
        onChange={(e) => setMinCA(e.target.value)} // Met à jour l'état à chaque changement
      />
      <ul>
        {filteredClients.length > 0 ? (
          filteredClients.map((client) => (
            <li key={client.id}>
              {client.nom} - {client.Tel} - CA: {client.CA}
            </li>
          ))
        ) : (
          <li>Aucun client trouvé avec ce CA.</li>
        )}
      </ul>
    </div>
  );
};

export default FiltreCa;
