function AfficherMasquer() {
  const [afficher, setAfficher] = useState();

  const controler = () => {
    if (afficher === true) {
      setAfficher(false);
    } else {
      setAfficher(true);
    }
  };
  return (
    <div>
      <button onClick={controler}>Afficher/Masquer</button>
      {afficher && <p>Contenu Ã  afficher</p>}
    </div>
  );
}

export default function App() {
