// store.js
import { legacy_createStore as createStore } from "redux";
import rootReducer from "./reducers"; // Assurez-vous que votre fichier reducers.js existe et est correctement importé.

const store = createStore(rootReducer); // Création du store avec le reducer

export default store; // Export du store
