import { AJOUTER, RETIRER, ENREGISTRER } from "./types";

const initialState = {
  transactions: [],
  compteur: 0,
  transactions
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case AJOUTER:
      return {
        ...state,
        transactions: [
          ...state.transactions,
          {
            type: "+",
            montant: action.payload,
            compteur: state.compteur + action.payload,
          },
        ],
        compteur: state.compteur + action.payload,
      };
    case RETIRER:
      return {
        ...state,
        transactions: [
          ...state.transactions,
          {
            type: "-",
            montant: action.payload,
            compteur: state.compteur - action.payload,
          },
        ],
        compteur: state.compteur - action.payload,
      };
    default:
      return state;
  }
};

export default reducer;
