import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setHotCafe, setLoading, setError, clearLoading } from "../redux/cafeSlice";
import axios from "axios";

const CoffeeCard = ({ coffee }) => {
  const dispatch = useDispatch();
  
  const handleOrder = () => {
    // Ajouter la vente au store
    const sale = {
      id: Date.now(), // Génère un ID unique
      coffeeType: coffee.name,
      quantity: 1, // Par défaut, quantité = 1
      price: coffee.price, // Vous devrez peut-être ajuster la structure des données
      total: coffee.price, // Total initial = prix unitaire
    };
    dispatch(addSale(sale)); // Ajouter la vente
  };

  return (
    <div className="col-md-4" key={coffee.id}>
      <div className="card mb-4 shadow-sm">
        <img src={coffee.image} alt={coffee.name} className="card-img-top" />
        <div className="card-body">
          <h5 className="card-title">{coffee.name}</h5>
          <p className="card-text">{coffee.description}</p>
          <button className="btn btn-primary" onClick={handleOrder}>
            Commander
          </button>
        </div>
      </div>
    </div>
  );
};

const HotCafe = () => {
  const dispatch = useDispatch();
  const { hotCafe, loading, error } = useSelector((state) => state.cafe);

  useEffect(() => {
    const fetchHotCafe = async () => {
      dispatch(setLoading());
      try {
        const response = await axios.get("https://api.sampleapis.com/coffee/hot");
        dispatch(setHotCafe(response.data));
      } catch (err) {
        dispatch(setError(err.message));
      } finally {
        dispatch(clearLoading());
      }
    };
    fetchHotCafe();
  }, [dispatch]);

  return (
    <div>
      <h1 className="mb-4">Hot Cafe</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div className="row">
          {hotCafe.map((item) => (
            <div className="col-md-4" key={item.id}>
              <div className="card mb-4 shadow-sm">
                <img src={item.image} alt={item.name} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p className="card-text">{item.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HotCafe;
