import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FormEquip from './FormEquip';
import EquipI from './EquipI';

function ListEquip() {
  const [equips, setEquips] = useState([]);
  const [filterReformed, setFilterReformed] = useState(false);
  const [sortBy, setSortBy] = useState('name');

  const addEquip = (equip) => {
    setEquips([...equips, { ...equip, id: Date.now() }]);
  };

  const reformEquip = (id) => {
    setEquips(equips.map(equip => equip.id === id ? { ...equip, isReformed: !equip.isReformed } : equip));
  };

  const deleteEquip = (id) => {
    setEquips(equips.filter(equip => equip.id !== id));
  };

  const filteredEquips = filterReformed ? equips.filter(equip => equip.isReformed) : equips;
  const sortedEquips = filteredEquips.sort((a, b) => a[sortBy].localeCompare(b[sortBy]));

  return (
    <div>
      <div>
        <label>
          <input
            type="checkbox"
            checked={filterReformed}
            onChange={() => setFilterReformed(!filterReformed)}
          />
          Afficher uniquement les équipements à réformer
        </label>
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="name">Trier par nom</option>
          <option value="purchaseDate">Trier par date d'achat</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Date d'achat</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {sortedEquips.map(equip => (
            <tr key={equip.id}>
              <td>{equip.name}</td>
              <td>{equip.purchaseDate ? equip.purchaseDate : 'N/A'}</td>
              <td>
                <button onClick={() => reformEquip(equip.id)}>A réformer</button>
                <button onClick={() => deleteEquip(equip.id)}>Supprimer</button>
                <Link to={{ pathname: `/details/${equip.id}`, state: { equips } }}>Voir les détails</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListEquip;
