import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; //* ðŸ”¹ Import pour redirection

export default function AjouterVoiture({ onAjout }) {
  const [nouvelleVoiture, setNouvelleVoiture] = useState({
    id: "",
    Marque: "",
    TypeCarburant: "",
    PrixLocation: "",
    image: "",
  });

  const navigate = useNavigate(); //* ðŸ”¹ Hook pour redirection

  const handleChange = (e) => {
    setNouvelleVoiture({
      ...setNouvelleVoiture,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    /*
     * âœ…EmpÃªche le rechargement de la page avec e.preventDefault()
     */
    e.preventDefault();

    if (nouvelleVoiture.id) {
      onAjout(nouvelleVoiture); //* ðŸ”¥ Envoie Ã  ListeVoitures
      setNouvelleVoiture({
        id: "",
        Marque: "",
        TypeCarburant: "",
        PrixLocation: "",
        image: "",
      });
      navigate("/"); //* ðŸ”¹ Redirection vers la liste aprÃ¨s ajout
    } else {
      alert("Veuillez remplir tous les champs obligatoires !");
    }
  };

  return (
    <div>
      <h1>Gestion des Voitures de Locations</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="id"
          placeholder="ID"
          value={nouvelleVoiture.id}
          onChange={handleChange}
        />
        <input
          type="text"
          name="Marque"
          placeholder="Marque"
          value={nouvelleVoiture.Marque}
          onChange={handleChange}
        />
        <input
          type="text"
          name="TypeCarburant"
          placeholder="Type Carburant"
          value={nouvelleVoiture.TypeCarburant}
          onChange={handleChange}
        />
        <input
          type="number"
          name="PrixLocation"
          placeholder="Prix de Location"
          value={nouvelleVoiture.PrixLocation}
          onChange={handleChange}
        />
        <input
          type="text"
          name="image"
          placeholder="Image URL"
          value={nouvelleVoiture.image}
          onChange={handleChange}
        />
        <button type="submit" style={{ background: "blue", color: "white" }}>
          Ajouter
        </button>
      </form>
    </div>
  );
}
