import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { removeItem } from '../';

const Cart = () => {
  const dispatch = useDispatch();
  const cart = useSelector(state => state.cart);

  const handleRemoveItem = (id) => {
    dispatch(removeItem(id));
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Panier</h2>
      <table className="table table-striped table-bordered table-hover">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Prix</th>
            <th>Quantit√©</th>
            <th>Total</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {cart.items.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.price} dh</td>
              <td>{item.quantity}</td>
              <td>{item.price * item.quantity} dh</td>
              <td>
                <button className="btn btn-danger" onClick={() => handleRemoveItem(item.id)}>Supprimer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Total: {cart.total} dh</h3>
    </div>
  );
};

export default Cart;
