// HotCafe.js
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setHotCafe, setLoading, setError, clearLoading } from "../redux/cafeSlice"; // Import necessary actions
import axios from "axios";
import CoffeeCard from "./CoffeeCard"; // Import CoffeeCard

const HotCafe = () => {
  const dispatch = useDispatch();
  const { hotCafe, loading, error } = useSelector((state) => state.cafe); // Corrected state variable

  useEffect(() => {
    const fetchHotCafe = async () => {
      dispatch(setLoading());
      try {
        const response = await axios.get("https://api.sampleapis.com/coffee/hot");
        dispatch(setHotCafe(response.data)); // Dispatch to update hotCafe in Redux store
      } catch (err) {
        dispatch(setError(err.message));
      } finally {
        dispatch(clearLoading());
      }
    };
    fetchHotCafe();
  }, [dispatch]);

  return (
    <div>
      <h1 className="mb-4">Hot Cafe</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div className="row">
          {hotCafe.map((item) => (
            <CoffeeCard key={item.id} coffee={item} /> // Render CoffeeCard for each hot coffee item
          ))}
        </div>
      )}
    </div>
  );
};

export default HotCafe;
