import React from "react";
import { useSelector } from "react-redux"; // Pour accéder aux données du store Redux

function Liste() {
  // Récupérer la liste des produits depuis le store Redux
  const produits = useSelector((state) => state.produits); // Remplacez `produits` par le nom exact de votre état

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Liste des Produits</h1>
      
      {/* Vérification si la liste est vide */}
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Nom</th>
              <th>Prix (MAD)</th>
              <th>Quantité</th>
            </tr>
          </thead>
          <tbody>
            {/* Parcourir la liste des produits */}
            {produits.map((produit, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{produit.nom}</td>
                <td>{produit.prix.toFixed(2)}</td>
                <td>{produit.quantite}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Liste;
