import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { ajouterTransaction, retirerTransaction, enregistrerTransactions } from '../redux/actions/action';

const Liste = () => {
  const transactions = useSelector(state => state.reducer.transactions);
  const compteur = useSelector(state => state.reducer.compteur);
  const dispatch = useDispatch();

  const handleAdd = (amount) => {
    dispatch(ajouterTransaction(amount));
  };

  const handleRemove = (amount) => {
    dispatch(retirerTransaction(amount));
  };

  const handleSave = () => {
    dispatch(enregistrerTransactions());
  };

  return (
    <div>
      <h2>Liste des Transactions</h2>
      <div>
        <button onClick={() => handleAdd(10)}>Ajouter 10</button>
        <button onClick={() => handleRemove(5)}>Retirer 5</button>
        <button onClick={handleSave}>Enregistrer Transactions</button>
      </div>
      <p>Solde : {compteur}</p>
      <ul>
        {transactions.map((transaction, index) => (
          <li key={index}>
            {transaction.type} {transaction.montant} (Solde: {transaction.compteur})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Liste;
