import { createSlice } from "@reduxjs/toolkit";

const cafeSlice = createSlice({
  name: "cafe",
  initialState: {
    iceCafe: [],
    hotCafe: [],
    sales: [], // Liste des ventes
    loading: false,
    error: null,
  },
  reducers: {
    setIceCafe: (state, action) => {
      state.iceCafe = action.payload;
    },
    setHotCafe: (state, action) => {
      state.hotCafe = action.payload;
    },
    addSale: (state, action) => {
      state.sales.push(action.payload); // Ajout d'une vente
    },
    setLoading: (state) => {
      state.loading = true;
    },
    clearLoading: (state) => {
      state.loading = false;
    },
    setError: (state, action) => {
      state.error = action.payload;
    },
  },
});

export const {
  setIceCafe,
  setHotCafe,
  addSale,
  setLoading,
  setError,
  clearLoading,
} = cafeSlice.actions;
export default cafeSlice.reducer;
