import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useNavigate,
} from "react-router-dom";
import FormProduit from "./components/FormProduct";
import ListProduit from "./components/ListProduit";
import Detail from "./components/detail";
import "bootstrap/dist/css/bootstrap.min.css";

/*
 ! --- Composant App ---
 * Point d'entrée principal de l'application
 * Configure les routes de l'application avec React Router
 * Inclut les composants FormProduit, ListProduit, et Detail
 */

const Main = () => {
  const navigate = useNavigate();

  const handleButtonClick = () => {
    navigate("/Detail/1"); // Navigate to Detail page with id 1
  };

  return (
    <div>
      <button className="btn btn-primary mb-3" onClick={handleButtonClick}>
        Go to Detail
      </button>
      <Routes>
        <Route
          path="/"
          element={
            <>
              <FormProduit />
              <ListProduit />
            </>
          }
        />
        <Route path="/Detail/:id" element={<Detail />} />
      </Routes>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Main />
    </Router>
  );
};

export default App;
