import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux"; // Importation des hooks
import { ajouterTransaction, retirerTransaction, enregistrerTransactions } from "../redux/action"; // Importation des actions

function Accueil() {
  const [nombre, setNombre] = useState(""); // État local pour le nombre à saisir
  const [operation, setOperation] = useState("+"); // État local pour l'opération (+ ou -)
  const compteur = useSelector((state) => state.compteur); // Récupérer le compteur du store
  const dispatch = useDispatch(); // Initialiser le dispatch

  const handleCalculer = () => {
    if (nombre) {
      const montant = parseFloat(nombre); // Convertir le nombre en float
      if (operation === "+") {
        dispatch(ajouterTransaction(montant)); // Envoie l'action "ajouter"
      } else {
        dispatch(retirerTransaction(montant)); // Envoie l'action "retirer"
      }
      setNombre(""); // Réinitialiser le champ de saisie
    } else {
      alert("Veuillez saisir un nombre !");
    }
  };

  return (
    <div>
      <h1 className="text-center mb-4">Gestion d'Argent de Poche</h1>
      <div className="card p-4 shadow">
        <div className="mb-3">
          <label className="form-label">Nombre:</label>
          <input
            type="number"
            className="form-control"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label me-2">Opération:</label>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              value="+"
              checked={operation === "+"}
              onChange={() => setOperation("+")}
            />
            <label className="form-check-label">Ajouter</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              value="-"
              checked={operation === "-"}
              onChange={() => setOperation("-")}
            />
            <label className="form-check-label">Retirer</label>
          </div>
        </div>
        <div className="d-grid">
          <button onClick={handleCalculer} className="btn btn-primary">
            Calculer
          </button>
          <button onClick={handleEnregistrer} className="btn btn-success mt-3">
            Enregistrer
          </button>
        </div>
      </div>
      <h2 className="text-center mt-4">Solde : {compteur} DH</h2>
    </div>
  );
}

export default Accueil;
