import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Accueil from '../components/accueil';  // Le composant d'accueil pour ajouter des transactions
import Liste from '../';     // Le composant pour afficher l'historique des transactions
import { Provider } from 'react-redux';
import store from './redux/store';  // Le store Redux pour gérer l'état global

function App() {
  return (
    <Provider store={store}> {/* Fournit le store Redux à toute l'application */}
      <Router>
        <Routes>
          <Route path="/" element={<Accueil />} />  {/* Route vers la page d'accueil */}
          <Route path="/liste" element={<Liste />} />  {/* Route vers la liste des transactions */}
        </Routes>
      </Router>
    </Provider>
  );
}

export default App;
