import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addSale } from "../redux/cafeSlice";

const Sales = () => {
  const [coffeeType, setCoffeeType] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState(0);
  const dispatch = useDispatch();
  const sales = useSelector((state) => state.cafe.sales);

  const handleAddSale = (e) => {
    e.preventDefault();
    if (coffeeType && quantity > 0 && price > 0) {
      const newSale = {
        id: Date.now(),
        coffeeType,
        quantity,
        price,
        total: quantity * price,
      };
      dispatch(addSale(newSale));
      setCoffeeType("");
      setQuantity(1);
      setPrice(0);
    }
  };

  const totalSales = sales.reduce((sum, sale) => sum + sale.total, 0);
  const totalQuantity = sales.reduce((sum, sale) => sum + sale.quantity, 0);

  return (
    <div>
      <h1>Gestion des Ventes</h1>
      <form onSubmit={handleAddSale} className="mb-4">
        <div className="mb-3">
          <label htmlFor="coffeeType" className="form-label">Type de Café</label>
          <input
            type="text"
            id="coffeeType"
            className="form-control"
            value={coffeeType}
            onChange={(e) => setCoffeeType(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="quantity" className="form-label">Quantité</label>
          <input
            type="number"
            id="quantity"
            className="form-control"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            min="1"
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="price" className="form-label">Prix Unitaire (MAD)</label>
          <input
            type="number"
            id="price"
            className="form-control"
            value={price}
            onChange={(e) => setPrice(Number(e.target.value))}
            min="1"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Ajouter Vente</button>
      </form>

      <h2>Historique des Ventes</h2>
      {sales.length > 0 ? (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Type de Café</th>
              <th>Quantité</th>
              <th>Prix Unitaire</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {sales.map((sale, index) => (
              <tr key={sale.id}>
                <td>{index + 1}</td>
                <td>{sale.coffeeType}</td>
                <td>{sale.quantity}</td>
                <td>{sale.price} MAD</td>
                <td>{sale.total} MAD</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Aucune vente enregistrée.</p>
      )}

      <h3 className="mt-4">Statistiques</h3>
      <p><strong>Quantité Totale :</strong> {totalQuantity}</p>
      <p><strong>Total des Ventes :</strong> {totalSales} MAD</p>
    </div>
  );
};

export default Sales;
