import React, { useState } from 'react';

export default function Voiture({ voituree, supprimer }) {
    
    const [isEditing, setIsEditing] = useState(false);
    const [editedVoiture, setEditedVoiture] = useState({ ...voituree });
    
    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleChange = (e) => {
        setEditedVoiture({
            ...editedVoiture,
            [e.target.name]: e.target.value,
        });
    };

    const handleSaveClick = () => {
        setIsEditing(false);
    };

    const discountedPrice = editedVoiture.discount
        ? editedVoiture.PrixLocation - (editedVoiture.PrixLocation * (editedVoiture.discount / 100))
        : editedVoiture.PrixLocation;

    return (
        <tr>
            <td>{voituree.id}</td>
            <td>{voituree.Marque}</td>
            <td>{voituree.TypeCarburant}</td>
            <td>
                
            </td>
            <td>
                <img src={voituree.image} alt={voituree.Marque} width="100" />
            </td>
            <td>
                {/* suppresion par confirmation  */}
                <button onClick={() => supprimer(voituree.id)}>
                    Supprimer
                </button> 
            </td>
        </tr>
    )
}
