import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const fetchHotCafes = createAsyncThunk("cafe/fetchHotCafes", async () => {
  const response = await axios.get("https://api.hotcafe.example.com");
  return response.data;
});

export const fetchIceCafes = createAsyncThunk("cafe/fetchIceCafes", async () => {
  const response = await axios.get("https://api.icecafe.example.com");
  return response.data;
});

const cafeSlice = createSlice({
  name: "cafe",
  initialState: {
    hotCafes: [],
    iceCafes: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchHotCafes.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchHotCafes.fulfilled, (state, action) => {
        state.loading = false;
        state.hotCafes = action.payload;
      })
      .addCase(fetchHotCafes.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })
      .addCase(fetchIceCafes.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchIceCafes.fulfilled, (state, action) => {
        state.loading = false;
        state.iceCafes = action.payload;
      })
      .addCase(fetchIceCafes.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      });
  },
});

export default cafeSlice.reducer;
