import React from 'react';
import { useDispatch, useSelector } from 'react-redux'; // Add useSelector
import { removeItem } from '../slices/cartSlice'; // Add this line

const Produit = ({ produit }) => {
  const dispatch = useDispatch();
  const cart = useSelector(state => state.cart); // Add this line

  const handleRemoveItem = () => {
    dispatch(removeItem(produit.id));
  };

  return (
    <div className="produit">
      <h3>{produit.name}</h3>
      <p>Prix: {produit.price} dh</p>
      <button onClick={handleRemoveItem}>Supprimer du panier</button>
    </div>
  );
};

export default Produit;
