// Sales.js (pas de changement majeur ici)
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { addSale } from "../redux/cafeSlice";

const Sales = () => {
  const dispatch = useDispatch();
  const sales = useSelector((state) => state.cafe.sales);

  const totalSales = sales.reduce((sum, sale) => sum + sale.total, 0);
  const totalQuantity = sales.reduce((sum, sale) => sum + sale.quantity, 0);

  return (
    <div>
      <h1>Gestion des Ventes</h1>
      <h2>Historique des Ventes</h2>
      {sales.length > 0 ? (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>#</th>
              <th>Type de Café</th>
              <th>Quantité</th>
              <th>Prix Unitaire</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {sales.map((sale, index) => (
              <tr key={sale.id}>
                <td>{index + 1}</td>
                <td>{sale.coffeeType}</td>
                <td>{sale.quantity}</td>
                <td>{sale.price} MAD</td>
                <td>{sale.total} MAD</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Aucune vente enregistrée.</p>
      )}

      <h3 className="mt-4">Statistiques</h3>
      <p><strong>Quantité Totale :</strong> {totalQuantity}</p>
      <p><strong>Total des Ventes :</strong> {totalSales} MAD</p>
    </div>
  );
};

export default Sales;
