import React from "react";
import { useSelector } from "react-router-dom";
import { Link, useParams } from "react-router-dom";

/*
 !    --- Composant detail ---
 * Acces au panier depuis le store Redux
 * Afficher les details du produit
 * Lien pour retourner à la liste des produits
*/

const Detail = () => {
  // * Acces au panier depuis le store Redux
  const panier = useSelector((state) => state.panier);

  const { id } = useParams(); //* Recuperer l'id du produit depuis l'url

  const produit = panier.find((item) => item.id === parseInt(id)); //* Recuperer le produit du panier

  // * Si le produit n'est pas trouvé, afficher un message d'erreur
  if (!produit) {
    return <p>Produit non trouve</p>;
  }
  return (
    <div>
      <h3>{produit.name}</h3>
      //* Afficher les details du produit
      <ul>
        <li key={produit.id + 1}>
          <strong>ID:</strong>
          {produit.id}
        </li>
        <li key={produit.id + 2}>
          <strong>Prix:</strong>
          {produit.prix}
        </li>
        <li key={produit.id + 3}>
          <strong>Quantité:</strong>
          {produit.qte}DH
        </li>
        <li key={produit.id + 4}>
          <strong>Montant Total :</strong>
          {produit.prix * produit.qte}DH
        </li>
      </ul>
      //* Lien pour retourner à la liste des produits
      <Link to="/">
        <button>Panier</button>
      </Link>
    </div>
  );
};
export default Detail;
